(43, 'Brittany Harkins sings ', 'banner_13353179414f9755b598af6.jpg', '<p><span style="font-family: Arial; "><span style="font-size: larger; ">Brittany Harkins does an amazing job singing Leona Lewis - &quot;Bleeding Love&quot;. </span></span></p>\r\n<p><span style="font-family: Arial; "><span style="font-size: larger; ">To watch the video go to The Arts category</span></span><a href="https://www.thesuperteens.com/homeindivtopic.php?bcid=5&amp;btid=107"><span style="font-family: Arial; "><span style="font-size: larger; "> or click on the link here.</span></span></a></p>', 'home', 'Inactive'),
(54, 'Providing Meaningful Service', 'banner_13364788794fa90c9fbe1b0.jpg', '<p><span style="font-family: Arial; "><span style="font-size: larger; ">Check out the simple way that these high school members of the Operation 4:12 Group, affected the life of a single lady that was struggling to make ends meet.</span></span></p>\r\n<p><span style="font-family: Arial; "><span style="font-size: larger; ">To view the video, go to The Super Teens In Action category, <a href="https://www.thesuperteens.com/homeindivtopic.php?bcid=1&amp;btid=127">or click on this link</a>.</span></span></p>', 'home', 'Active'),
(9, 'The Super Teens Registered Members', 'banner_13328496884f71ac18e470d.jpg', '<p><span style="font-family: Arial; "><span style="font-size: larger; ">&nbsp;Membership in TheSuperTeens.com has its'' priveleges.&nbsp;</span></span></p>\r\n<p><span style="font-family: Arial; "><span style="font-size: larger; "><br type="_moz" />\r\n</span></span></p>\r\n<p><span style="font-family: Arial; "><span style="font-size: larger; ">Membership at TheSuperTeens.com website has its'' privleges. Registered members are allowed to post comments on articles and blogs, and can post comments and start new conversations in the teen forum. There is no cost to sign up as a member of TheSuperTeens.com. &nbsp;All you will need is a user name, a password, and a valid email address, as the activation code will be sent to your email address, and must be activated before you can post to the site. &nbsp;Your email address will not be shared with anyone and you won''t receive any emails from TheSuperTeens.com unless there is a problem with your account or if you sign up for our newsletter.</span></span></p>', 'home', 'Inactive'),
(51, 'Where Was This Picture Taken', 'banner_13363908644fa7b4d076e27.jpg', '<p><span style="font-family: Arial; "><span style="font-size: larger; ">Where was this picture taken?</span></span></p>\r\n<p><span style="font-family: Arial; "><span style="font-size: larger; ">To see the picture in full scale, go to the Where Are The Super Teens category, <a href="https://www.thesuperteens.com/homeindivtopic.php?bcid=2&amp;btid=123">or click on this link here</a>.</span></span></p>', 'home', 'Active'),
(50, '5 Quick Treats For School Lunches', 'banner_13360711204fa2d3d089d76.jpg', '<p>&nbsp;Here are some great ideas for that quick on-the-go snack for your school lunches.</p>\r\n<p>To watch the video go to the Health and Nutrition category <a href="https://www.thesuperteens.com/homeindivtopic.php?bcid=8&amp;btid=119">or click on this link.</a></p>', 'home', 'Inactive'),
(55, '7'' 5" High School Basketball Player', 'banner_13365661284faa6170bb88a.jpg', '<p><span style="font-family: Arial; "><span style="font-size: larger; ">You''ve got to see this 7'' 5&quot; high school basketball player in action.</span></span></p>\r\n<p><span style="font-family: Arial; "><span style="font-size: larger; ">To see the video, go to the Sports section,<a href="https://www.thesuperteens.com/homeindivtopic.php?bcid=7&amp;btid=129"> or click on the link here</a>.</span></span></p>', 'home', 'Active'),
(53, 'Tell Us What You Want', 'banner_13364758134fa900a57e592.jpg', '<p><span style="font-family: Arial; "><span style="font-size: larger; ">Now is your opportunity to tell us what kind of content you want to see on your site.</span></span></p>\r\n<p><span style="font-family: Arial; "><span style="font-size: larger; ">Go to the Tell Us What You Want category, <a href="https://www.thesuperteens.com/homeindivtopic.php?btid=126">or click on the link here</a>.</span></span></p>', 'home', 'Active');

